if (trigger.args.hasOwnProperty('error')){
  const totalRecords = parseInt(`${trigger.args.recordsCount}`+`${trigger.args.recordsFailedCount}`);
  notify.email(config.email, `Error for Bulk Upload Job with Id ${trigger.args.id}`, `The Bulk Upload Job with Id ${trigger.args.id} failed with error message: ${trigger.args.error} and status: ${trigger.args.status}. Of ${totalRecords} records, ${trigger.args.recordsFailedCount} failed to upload.`);
} else {
  done({ success: true });
}
